import React, { useState, useEffect } from "react";
import TransactionsList from "./TransactionsList";
import Search from "./Search";
import AddTransactionForm from "./AddTransactionForm";

function AccountContainer() {
  const [transactions, setTransactions] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");

  useEffect(() => {
    fetch("http://localhost:8001/transactions")
      .then(response => response.json())
      .then(data => setTransactions(data));
  }, []);

  function updatedTransactionEvents(addedTransactions) {
    const updatedTransactions = [...transactions, addedTransactions];
    setTransactions(updatedTransactions);
  }

  function handleSearch(searchQuery) {
    setSearchQuery(searchQuery);
  }

  return (
    <div>
      <Search searchEvent={searchQuery} setSearchEvent={handleSearch} />
      <AddTransactionForm addedData={updatedTransactionEvents} />
      <TransactionsList transactions={transactions} searchQuery={searchQuery} />
    </div>
  );
}

export default AccountContainer;
